package _07_Conditionals;

public class _01_if {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
	boolean meatloafForLunch=true;
	boolean tacosForLunch=true;
	boolean iceCreamInFreezer=false;
		
		if (meatloafForLunch) {
			System.out.println("I am going to bring my lunch that day.");
		}
		
		if (tacosForLunch) {
			System.out.println("Taco Tuesdays are my favorite.");
			
		}
		
		if (!iceCreamInFreezer) {
			System.out.println("Please pick up some Rocky Road on your way home from school.");
			
		}
		
		

	}

}


